export function generateFakeData(
	filterStr = "",
	shouldReject = false
): Promise<{ books: Book[] }> {
	// Simulate an error condition
	if (shouldReject) {
		return new Promise((_, reject) => {
			setTimeout(() => {
				reject(new Error("Failed to generate fake data"));
			}, 2000); // Simulating a delay of 1 second
		});
	}

	// Simulate some asynchronous operation, e.g., fetching data from an API
	return new Promise((resolve) => {
		setTimeout(() => {
			const books = res.books.filter((book) => {
				if (filterStr.length <= 3) {
					return true;
				}
				return book.title
					.toLowerCase()
					.includes(filterStr.toLowerCase());
			});
			resolve({ books });
		}, 2000); // Simulating a delay of 1 second
	});
}
